import React, { useState } from "react";
import { useNavigate } from "react-router";
import $ from "jquery";


export default function Create() {
 const [form, setForm] = useState({
  classID: "",
  reviewID: "",
  stars: "",
  initialReview: "",
  sentiment: ""
 });
 const navigate = useNavigate();
 let url = 'https://sis.jhu.edu/api/classes/Whiting%20School%20of%20Engineering/EN%20Computer%20Science/current?key=Iuw2pbsrh81TROyqQ7pwPRyfx7ha6XiN';

 function loadJSON(path, success, error) {
  var xhr = new XMLHttpRequest();
  xhr.onreadystatechange = function () {
    if (xhr.readyState === 4) {
      if (xhr.status === 200) {
        success(JSON.parse(xhr.responseText));
      }
      else {
        error(xhr);
      }
    }
  };
  xhr.open('GET', path, true);
  xhr.send();
}

loadJSON(url, myData, 'jsonp');

function myData(Data)
{

  // Output only the details on the first post
  console.log(Data[0]);

  // output the details of first three posts
  console.log("First three posts");
  for(var i=0; i<3; i=i+1)
  {
    console.log(Data[i]);
  } 
  // output the id field of first five elements. 
  console.log("First five ID");
  for(var i=0; i<5; i=i+1)
  {
    console.log(Data[i].id);
  }
}

 // These methods will update the state properties.
 function updateForm(value) {
   return setForm((prev) => {
     return { ...prev, ...value };
   });
 }

 // This function will handle the submission.
 async function onSubmit(e) {
   e.preventDefault();

   // When a post request is sent to the create url, we'll add a new record to the database.
   const newRecord = { ...form };

   await fetch("http://localhost:1000/record/add", {
     method: "POST",
     headers: {
       "Content-Type": "application/json",
     },
     body: JSON.stringify(newRecord),
   })
   .catch(error => {
     window.alert(error);
     return;
   });

   setForm({ classID: "", reviewID: "", stars: "", initialReview: "", sentiment: "" });
   navigate("/");
 }

 // This following section will display the form that takes the input from the user.
 return (
   <div>
     <h3>Courses</h3>
     <form onSubmit={onSubmit}>
       <div className="form-group">
         <label htmlFor="reviewID">reviewID</label>
         <input
           type="text"
           className="form-control"
           id="reviewID"
           value={form.reviewID}
           onChange={(e) => updateForm({ reviewID: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="stars">stars</label>
         <input
           type="text"
           className="form-control"
           id="stars"
           value={form.stars}
           onChange={(e) => updateForm({ stars: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="initialReview">initialReview</label>
         <input
           type="text"
           className="form-control"
           id="initialReview"
           value={form.initialReview}
           onChange={(e) => updateForm({ initialReview: e.target.value })}
         />
       </div>
       <div className="form-group">
         <label htmlFor="sentiment">sentiment</label>
         <input
           type="text"
           className="form-control"
           id="sentiment"
           value={form.sentiment}
           onChange={(e) => updateForm({ sentiment: e.target.value })}
         />
       </div>
     </form>
   </div>
 );
}
